    i = 0
    while i < len(words):
        if i < len(words) - 1 and (words[i] + words[i + 1]) in colors:
            merged_words.append(words[i] + words[i + 1])
            i += 2
        elif i < len(words) - 1 and (words[i] + words[i + 1]) in days:
            merged_words.append(words[i] + words[i + 1])
            i += 2
        elif words[i] in time_words:
            merged_words.append(time_words[words[i]])
            i += 1
        else:
            merged_words.append(words[i])
            i += 1

    tagged_words = pos_tag(merged_words, corpus="orchid_ud")

    subject, pronoun, adverbs, verbs, objects, prepositions, others = [], [], [], [], [], [], []
    question_mark, negation = "", ""
    condition = []
    negations = []

    for idx, (word, tag) in enumerate(tagged_words):
    # üîπ ‡∏ñ‡πâ‡∏≤‡∏Ñ‡∏≥‡∏Å‡πà‡∏≠‡∏ô‡∏´‡∏ô‡πâ‡∏≤‡πÄ‡∏õ‡πá‡∏ô "‡∏û‡∏£‡∏∞" ‡πÅ‡∏•‡∏∞‡∏Ñ‡∏≥‡∏õ‡∏±‡∏à‡∏à‡∏∏‡∏ö‡∏±‡∏ô‡πÄ‡∏õ‡πá‡∏ô "‡∏â‡∏±‡∏ô" ‚Üí ‡πÄ‡∏õ‡∏•‡∏µ‡πà‡∏¢‡∏ô "‡∏â‡∏±‡∏ô" ‡πÄ‡∏õ‡πá‡∏ô "‡∏Å‡∏¥‡∏ô"
        if idx > 0 and tagged_words[idx - 1][0] == "‡∏û‡∏£‡∏∞" and word == "‡∏â‡∏±‡∏ô":
            word = "‡∏Å‡∏¥‡∏ô"

    # üîπ ‡∏ñ‡πâ‡∏≤‡∏Ñ‡∏≥‡∏Å‡πà‡∏≠‡∏ô‡∏´‡∏ô‡πâ‡∏≤‡πÄ‡∏õ‡πá‡∏ô "‡∏û‡∏£‡∏∞" ‡πÅ‡∏•‡∏∞‡∏Ñ‡∏≥‡∏õ‡∏±‡∏à‡∏à‡∏∏‡∏ö‡∏±‡∏ô‡πÄ‡∏õ‡πá‡∏ô "‡πÑ‡∏°‡πà" ‚Üí ‡πÄ‡∏ä‡πá‡∏Ñ‡∏Ñ‡∏≥‡∏ñ‡∏±‡∏î‡πÑ‡∏õ‡πÄ‡∏õ‡πá‡∏ô "‡∏â‡∏±‡∏ô"
        if idx > 0 and tagged_words[idx - 1][0] == "‡∏û‡∏£‡∏∞" and word == "‡πÑ‡∏°‡πà":
            if idx < len(tagged_words) - 1 and tagged_words[idx + 1][0] == "‡∏â‡∏±‡∏ô":
                word = "‡∏Å‡∏¥‡∏ô ‡πÑ‡∏°‡πà"  # ‡πÄ‡∏õ‡∏•‡∏µ‡πà‡∏¢‡∏ô "‡πÑ‡∏°‡πà" ‡πÄ‡∏õ‡πá‡∏ô "‡πÑ‡∏°‡πà‡∏Å‡∏¥‡∏ô"
                tagged_words[idx + 1] = ("", "")  # ‡∏•‡∏ö "‡∏â‡∏±‡∏ô" ‡∏≠‡∏≠‡∏Å‡∏à‡∏≤‡∏Å list

        if word == "‡∏ñ‡πâ‡∏≤":
            condition.append(word)
            continue

        if word == "‡∏Ñ‡∏ß‡∏£":
            adverbs.insert(0, word)  # ‡∏ó‡∏≥‡πÉ‡∏´‡πâ "‡∏Ñ‡∏ß‡∏£" ‡∏≠‡∏¢‡∏π‡πà‡∏´‡∏ô‡πâ‡∏≤‡∏Å‡∏£‡∏¥‡∏¢‡∏≤
            continue

        if word in adverbs_to_change:
            word = "‡∏Å‡∏±‡∏ô"

        if word in question_words:
            question_mark = "?"
            continue

        if word in words_to_remove:
            continue

        if word in {"‡πÑ‡∏°‡πà"}:
            negations.append(word)
            continue

        if tag.startswith("VERB"):
            verbs.append(word)
        elif tag.startswith("ADV"):
            adverbs.append(word)
        elif tag.startswith("NOUN"):
            # ‡πÅ‡∏¢‡∏Å‡πÅ‡∏¢‡∏∞‡∏ß‡πà‡∏≤‡πÄ‡∏õ‡πá‡∏ô **‡∏õ‡∏£‡∏∞‡∏ò‡∏≤‡∏ô (subject) ‡∏´‡∏£‡∏∑‡∏≠ ‡∏Å‡∏£‡∏£‡∏° (object)**
            if not verbs:  # ‡∏ñ‡πâ‡∏≤‡∏¢‡∏±‡∏á‡πÑ‡∏°‡πà‡∏°‡∏µ verb ‡πÅ‡∏™‡∏î‡∏á‡∏ß‡πà‡∏≤‡πÄ‡∏õ‡πá‡∏ô‡∏õ‡∏£‡∏∞‡∏ò‡∏≤‡∏ô
                subject.append(word)
            else:
                objects.append(word)
        elif tag.startswith("PRON"):
            # ‡∏ñ‡πâ‡∏≤‡∏Ñ‡∏≥‡πÄ‡∏õ‡πá‡∏ô‡∏™‡∏£‡∏£‡∏û‡∏ô‡∏≤‡∏° ‡πÉ‡∏´‡πâ‡∏ß‡∏≤‡∏á‡πÄ‡∏õ‡πá‡∏ô **‡∏õ‡∏£‡∏∞‡∏ò‡∏≤‡∏ô**
            subject.append(word)
        elif tag.startswith("ADP"):
            prepositions.append(word)
        else:
            others.append(word)

    # üîπ ‡∏õ‡∏£‡∏±‡∏ö‡∏Å‡∏≤‡∏£‡πÄ‡∏£‡∏µ‡∏¢‡∏á‡∏õ‡∏£‡∏∞‡πÇ‡∏¢‡∏Ñ‡πÄ‡∏õ‡πá‡∏ô TSL syntax
    new_sentence = condition + subject + pronoun + objects + adverbs + prepositions + others + verbs  # ‡∏Å‡∏£‡∏¥‡∏¢‡∏≤‡πÑ‡∏õ‡∏´‡∏•‡∏±‡∏á‡∏™‡∏∏‡∏î

    # üîπ ‡πÉ‡∏´‡πâ‡∏Ñ‡∏≥‡∏ß‡πà‡∏≤ "‡πÑ‡∏°‡πà" ‡∏≠‡∏¢‡∏π‡πà‡∏´‡∏ô‡πâ‡∏≤‡∏Å‡∏£‡∏¥‡∏¢‡∏≤
    if negations and verbs:
        new_sentence.insert(new_sentence.index(verbs[0]), negations[0])
    elif negations:
        new_sentence += negations

    if question_mark:
        new_sentence.append(question_mark)
    # üîπ **‡∏™‡∏•‡∏±‡∏ö‡∏Ñ‡∏≥ "‡πÑ‡∏°‡πà ‡∏Å‡∏¥‡∏ô" ‚Üí "‡∏Å‡∏¥‡∏ô ‡πÑ‡∏°‡πà"**
    for i in range(len(new_sentence) - 1):
        if new_sentence[i] == "‡πÑ‡∏°‡πà" and new_sentence[i + 1] == "‡∏Å‡∏¥‡∏ô":
            new_sentence[i], new_sentence[i + 1] = new_sentence[i + 1], new_sentence[i]

    return " ".join(new_sentence)

